mapper.select:
SELECT * FROM ( SELECT U.*, ROWNUM RN FROM (
	SELECT * FROM USERS


			ID IN

				#{item}



			AND ID LIKE '%' || #{searchKeyword} || '%'


			AND NAME LIKE '%' || #{searchKeyword} || '%'		


	ORDER BY ID
) U ) A WHERE RN BETWEEN #{start} and #{end}

mapper.update:
UPDATE USERS SET PASSWORD = #{password},
				 NAME = #{name},
				 ROLE = #{role}
	WHERE ID = #{id}
